import React, { useState, useEffect } from "react";
import axios from "axios";
import {
    Container,
    Typography,
    Paper,
    Box,
    Grid,
    Card,
    CardContent,
    CardMedia,
    IconButton,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";

export const API_BASE_URL = "https://spotdiff.ru/fuji-api";

const FoodPage = () => {
    const [foods, setFoods] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedCategoryId, setSelectedCategoryId] = useState(1); // Default selected category
    const [openDialog, setOpenDialog] = useState(false); // Dialog open state
    const [selectedFood, setSelectedFood] = useState(null); // Selected food for dialog

    // Fetch food items from the API
    useEffect(() => {
        const fetchFoods = async () => {
            try {
                const response = await axios.get(API_BASE_URL + '/foods?categoryId=' + selectedCategoryId, {
                    headers: {
                        'Authorization': 'Bearer ' + localStorage.getItem('access_token')
                    }
                })
                setFoods(response.data);
            } catch (error) {
                console.error("Error fetching foods:", error);
            }
        };
        fetchFoods().then();
    }, [selectedCategoryId]); // Refetch foods whenever the selected category changes

    // Fetch categories from the API
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await axios.get(API_BASE_URL + '/foods/categories', {
                    headers: {
                        'Authorization': 'Bearer ' + localStorage.getItem('access_token')
                    }
                })
                setCategories(response.data);
            } catch (error) {
                console.error("Error fetching categories:", error);
            }
        };
        fetchCategories().then();
    }, []);

    // Handle category selection
    const handleCategorySelect = (categoryId) => {
        setSelectedCategoryId(categoryId);
    };

    // Handle food item click
    const handleFoodClick = (food) => {
        setSelectedFood(food);
        setOpenDialog(true);
    };

    // Handle dialog close
    const handleDialogClose = () => {
        setOpenDialog(false);
        setSelectedFood(null); // Clear the selected food when closing the dialog
    };

    return (
        <Box
            sx={{
                minHeight: "100vh",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                background: "linear-gradient(135deg, #1E1B4B, #3E1E68, #6A1B9A)",
            }}
        >
            <Container maxWidth="lg">
                <Paper
                    elevation={10}
                    sx={{
                        padding: 5,
                        borderRadius: 3,
                        backdropFilter: "blur(10px)",
                        backgroundColor: "rgba(255, 255, 255, 0.1)",
                        boxShadow: "0px 4px 20px rgba(0, 0, 0, 0.3)",
                        border: "1px solid rgba(255, 255, 255, 0.3)",
                        margin: "50px"
                    }}
                >
                    <Typography variant="h4" component="h1" sx={{ fontWeight: "bold", color: "#fff" }}>
                        Фуджи
                    </Typography>
                    <Typography variant="h7" component="h4" sx={{ color: "#fff", marginTop: 1 }}>
                        Доставка еды
                    </Typography>

                    {/* Horizontal list of categories */}
                    <Box sx={{ marginTop: 2, overflowX: "auto", paddingBottom: 2 }}>
                        <Box sx={{ display: "flex", gap: 2 }}>
                            {categories.map((category) => (
                                <Button
                                    key={category.id}
                                    sx={{
                                        backgroundColor: selectedCategoryId === category.id ? "#5A1489" : "#6A1B9A", // Highlight selected category
                                        color: "#fff",
                                        borderRadius: "20px",  // More rounded corners
                                        padding: "10px 20px", // Larger padding for better touch area
                                        fontSize: "1rem", // Slightly larger font size
                                        boxShadow: selectedCategoryId === category.id ? "0px 4px 12px rgba(0, 0, 0, 0.2)" : "none", // Shadow for selected button
                                        border: selectedCategoryId === category.id ? "2px solid #fff" : "none", // White border for selected category
                                        "&:hover": {
                                            backgroundColor: "#5A1489", // Hover effect
                                        },
                                        transition: "background-color 0.3s ease, box-shadow 0.3s ease, border 0.3s ease", // Smooth transition
                                    }}
                                    onClick={() => handleCategorySelect(category.id)}
                                >
                                    {category.name}
                                </Button>
                            ))}
                        </Box>
                    </Box>

                    <Grid container spacing={3} sx={{ marginTop: 3 }}>
                        {foods.map((food) => (
                            <Grid item xs={12} sm={6} md={4} key={food.id}>
                                <Card
                                    sx={{
                                        borderRadius: 3,
                                        overflow: "hidden",
                                        backdropFilter: "blur(10px)",
                                        backgroundColor: "rgba(255, 255, 255, 0.1)",
                                        boxShadow: "0px 4px 15px rgba(0, 0, 0, 0.2)",
                                    }}
                                    onClick={() => handleFoodClick(food)} // Open dialog on food item click
                                >
                                    <CardMedia
                                        component="img"
                                        alt={food.title}
                                        height="200"
                                        image={food.image}
                                    />
                                    <CardContent sx={{ color: "#fff" }}>
                                        <Typography variant="h6">{food.title}</Typography>
                                        <Typography variant="body2" sx={{ color: "#bbb" }}>
                                            {food.description}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: "#bbb" }}>
                                            {food.allergens}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: "#fff", fontWeight: "bold" }}>
                                            {food.price} ₽
                                        </Typography>
                                        {food.cardCount > 0 && (
                                            <Typography variant="body2" sx={{ color: "#5C6BC0", fontWeight: "bold" }}>
                                                В корзине: {food.cardCount} шт.
                                            </Typography>
                                        )}
                                    </CardContent>
                                    <Box sx={{ display: "flex", justifyContent: "space-between", padding: 1 }}>
                                        <IconButton sx={{ color: food.favorited ? "#ff4081" : "#bbb" }}>
                                            <FavoriteIcon />
                                        </IconButton>
                                        <IconButton
                                            sx={{
                                                color: food.cardCount > 0 ? "#8e24aa" : "#bbb",
                                            }}
                                        >
                                            <ShoppingCartIcon />
                                        </IconButton>
                                    </Box>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>
                </Paper>

                {/* Dialog to display food details */}
                <Dialog open={openDialog} onClose={handleDialogClose}>
                    <DialogTitle>{selectedFood ? selectedFood.title : "Loading..."}</DialogTitle>
                    <DialogContent sx={{ color: "#333" }}>
                        {selectedFood ? (
                            <>
                                <Typography variant="body1" sx={{ marginBottom: 2 }}>
                                    {selectedFood.description}
                                </Typography>
                                <Typography variant="body2">
                                    Цена: <strong>{selectedFood.price} ₽</strong>
                                </Typography>
                                <Typography variant="body2">
                                    Аллергены: {selectedFood.allergens || "Нет данных"}
                                </Typography>
                            </>
                        ) : (
                            <Typography variant="body2">Загрузка...</Typography>
                        )}
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleDialogClose} color="primary">
                            Закрыть
                        </Button>
                    </DialogActions>
                </Dialog>
            </Container>
        </Box>
    );
};

export default FoodPage;
