import React, {useEffect, useState} from "react";
import {
    Container,
    Typography,
    Paper,
    Box,
    Grid,
    Card,
    CardContent,
    CardMedia,
    IconButton,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    Divider,
    TextField,
} from "@mui/material";
import {ShoppingCart as ShoppingCartIcon} from "@mui/icons-material";
import axios from "axios";
import {API_BASE_URL} from "./MainPage";

const CartPage = () => {
    const [cartItems, setCartItems] = useState([
        // Sample data for cart
        {
            id: 0,
            title: "Pizza Margherita",
            description: "Classic Italian pizza",
            allergens: "Gluten",
            grams: 250,
            price: 500,
            image: "https://via.placeholder.com/150",
            favorited: true,
            category: {id: 1, name: "Pizza"},
            cardCount: 2,
        },
        {
            id: 1,
            title: "Sushi Rolls",
            description: "Fresh sushi rolls",
            allergens: "Fish",
            grams: 150,
            price: 350,
            image: "https://via.placeholder.com/150",
            favorited: false,
            category: {id: 2, name: "Sushi"},
            cardCount: 1,
        },
    ]);

    useEffect(() => {
        axios.get(API_BASE_URL + '/foods/cards', {
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('access_token')
            }
        }).then(response => {
            setCartItems(response.data)
        })
    }, [])

    const [openOrderDialog, setOpenOrderDialog] = useState(false);
    const [address, setAddress] = useState({
        city: "",
        street: "",
        house: "",
        apartment: "",
        phone: "",
    });

    const handleOrderDialogClose = () => setOpenOrderDialog(false);
    const handleOrderDialogOpen = () => setOpenOrderDialog(true);

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setAddress((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handlePlaceOrder = () => {
        // Handle order placement logic here, e.g., API call to submit the order
        console.log("Order placed:", address);
        handleOrderDialogClose(); // Close the dialog
    };

    return (
        <Box
            sx={{
                minHeight: "100vh",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
            }}
        >
            <Container maxWidth="lg">
                <Paper
                    elevation={10}
                    sx={{
                        padding: 5,
                        borderRadius: 3,
                        backdropFilter: "blur(10px)",
                        backgroundColor: "rgba(255, 255, 255, 0.1)",
                        boxShadow: "0px 4px 20px rgba(0, 0, 0, 0.3)",
                        border: "1px solid rgba(255, 255, 255, 0.3)",
                        margin: "50px",
                    }}
                >
                    <Typography variant="h4" component="h1" sx={{fontWeight: "bold", color: "#fff"}}>
                        Корзина
                    </Typography>
                    <Typography variant="h7" component="h4" sx={{color: "#fff", marginTop: 1}}>
                        Ваши товары
                    </Typography>

                    <Grid container spacing={3} sx={{marginTop: 3}}>
                        {cartItems.map((item) => (
                            <Grid item xs={12} sm={6} md={4} key={item.id}>
                                <Card
                                    sx={{
                                        borderRadius: 3,
                                        overflow: "hidden",
                                        backdropFilter: "blur(10px)",
                                        backgroundColor: "rgba(255, 255, 255, 0.1)",
                                        boxShadow: "0px 4px 15px rgba(0, 0, 0, 0.2)",
                                        cursor: "pointer",
                                    }}
                                >
                                    <CardMedia
                                        component="img"
                                        alt={item.title}
                                        image={item.image}
                                        sx={{
                                            height: 270,
                                            objectFit: "cover",
                                            width: "100%",
                                        }}
                                    />
                                    <CardContent sx={{color: "#fff"}}>
                                        <Typography variant="h6">{item.title}</Typography>
                                        <Typography variant="body2" sx={{color: "#bbb"}}>
                                            {item.description}
                                        </Typography>
                                        <Typography variant="body2" sx={{color: "#bbb"}}>
                                            {item.allergens}
                                        </Typography>
                                        <Typography variant="body2" sx={{color: "#fff", fontWeight: "bold"}}>
                                            {item.price} ₽
                                        </Typography>
                                        <Typography variant="body2" sx={{color: "#bbb"}}>
                                            Количество: {item.cardCount}
                                        </Typography>
                                    </CardContent>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>

                    <Box sx={{display: "flex", justifyContent: "center", marginTop: 3}}>
                        <Button
                            variant="contained"
                            sx={{
                                backgroundColor: "#6A1B9A",
                                color: "#fff",
                                padding: "10px 20px",
                                borderRadius: "20px",
                                fontWeight: "bold",
                                "&:hover": {
                                    backgroundColor: "#5A1489",
                                },
                            }}
                            onClick={handleOrderDialogOpen}
                        >
                            Оформить заказ
                            <ShoppingCartIcon sx={{marginLeft: 1}}/>
                        </Button>
                    </Box>
                </Paper>

                {/* Order Dialog */}
                <Dialog open={openOrderDialog} onClose={handleOrderDialogClose}>
                    <DialogTitle sx={{backgroundColor: "#6A1B9A", color: "#fff", padding: "16px 24px"}}>
                        Оформление заказа
                    </DialogTitle>

                    <DialogContent sx={{backgroundColor: "#fff", padding: 3}}>
                        <TextField
                            fullWidth
                            label="Город"
                            variant="outlined"
                            margin="normal"
                            name="city"
                            value={address.city}
                            onChange={handleInputChange}
                        />
                        <TextField
                            fullWidth
                            label="Улица"
                            variant="outlined"
                            margin="normal"
                            name="street"
                            value={address.street}
                            onChange={handleInputChange}
                        />
                        <TextField
                            fullWidth
                            label="Дом"
                            variant="outlined"
                            margin="normal"
                            name="house"
                            value={address.house}
                            onChange={handleInputChange}
                        />
                        <TextField
                            fullWidth
                            label="Квартира"
                            variant="outlined"
                            margin="normal"
                            name="apartment"
                            value={address.apartment}
                            onChange={handleInputChange}
                        />
                        <TextField
                            fullWidth
                            label="Номер телефона"
                            variant="outlined"
                            margin="normal"
                            name="phone"
                            value={address.phone}
                            onChange={handleInputChange}
                        />
                    </DialogContent>

                    <DialogActions sx={{padding: "8px 24px", backgroundColor: "#f5f5f5"}}>
                        <Button
                            onClick={handleOrderDialogClose}
                            sx={{
                                color: "#6A1B9A",
                                fontWeight: "bold",
                                borderRadius: "20px",
                                padding: "8px 20px",
                                "&:hover": {
                                    backgroundColor: "#6A1B9A",
                                    color: "#fff",
                                },
                            }}
                        >
                            Отмена
                        </Button>
                        <Button
                            onClick={handlePlaceOrder}
                            sx={{
                                color: "#fff",
                                backgroundColor: "#6A1B9A",
                                fontWeight: "bold",
                                borderRadius: "20px",
                                padding: "8px 20px",
                                "&:hover": {
                                    backgroundColor: "#5A1489",
                                },
                            }}
                        >
                            Оформить
                        </Button>
                    </DialogActions>
                </Dialog>
            </Container>
        </Box>
    );
};

export default CartPage;
