import React, { useState } from "react";
import axios from "axios";
import {
    Container,
    TextField,
    Button,
    Typography,
    Paper,
    Box
} from "@mui/material";

export const API_BASE_URL = "https://spotdiff.ru/fuji-api";

const AuthPage = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorText, setErrorText] = useState("");

    async function auth() {
        setErrorText("");

        if (username === "" || password === "") {
            setErrorText("Заполните все поля");
        } else {
            try {
                const response = await axios.post(API_BASE_URL + "/foods/login", {
                    username,
                    password,
                });
                localStorage.setItem("access_token", response.data.accessToken);
                window.location.reload();
            } catch (e) {
                console.error(e);
                setErrorText("Ошибка входа. Проверьте данные.");
            }
        }
    }

    async function reg() {
        setErrorText("");

        if (username === "" || password === "") {
            setErrorText("Заполните все поля");
        } else {
            try {
                const response = await axios.post(API_BASE_URL + "/foods/register", {
                    username,
                    password,
                });
                localStorage.setItem("access_token", response.data.accessToken);
                window.location.reload();
            } catch (e) {
                console.error(e);
                setErrorText("Ошибка регистрации. Попробуйте снова.");
            }
        }
    }

    return (
        <Container maxWidth="xs">
            <Paper elevation={3} sx={{ padding: 4, marginTop: 8, textAlign: "center" }}>
                <img src="/logo.png" style={{width: "90px", height: "90px"}}/>
                <Typography variant="h4" component="h1" gutterBottom>
                    Фуджи
                </Typography>
                <Typography variant="subtitle1" color="text.secondary">
                    Вход в личный кабинет
                </Typography>

                {errorText && (
                    <Typography color="error" sx={{ marginTop: 2 }}>
                        {errorText}
                    </Typography>
                )}

                <Box sx={{ display: "flex", flexDirection: "column", gap: 2, marginTop: 3 }}>
                    <TextField
                        label="Имя пользователя"
                        variant="outlined"
                        fullWidth
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />

                    <TextField
                        label="Пароль"
                        type="password"
                        variant="outlined"
                        fullWidth
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <Button variant="contained" color="primary" fullWidth onClick={auth}>
                        Войти
                    </Button>
                    <Button variant="outlined" color="secondary" fullWidth onClick={reg}>
                        Создать аккаунт
                    </Button>
                </Box>
            </Paper>
        </Container>
    );
};

export default AuthPage;
