import React from 'react';
import { Box, Typography, TextField, Button, CircularProgress } from '@mui/material';
import Card from '../components/Card';

function Home({
                items,
                searchValue,
                setSearchValue,
                onChangeSearchInput,
                onAddToFavorite,
                onAddToCart,
                isLoading,
              }) {
  const renderItems = () => {
    const filteredItems = items.filter((item) =>
        item.title.toLowerCase().includes(searchValue.toLowerCase())
    );
    return (isLoading ? [...Array(8)] : filteredItems).map((item, index) => (
        <Card
            key={index}
            onFavorite={(obj) => onAddToFavorite(obj)}
            onPlus={(obj) => onAddToCart(obj)}
            loading={isLoading}
            {...item}
        />
    ));
  };

  return (
      <Box sx={{ background: 'linear-gradient(135deg, #1E1B4B, #3E1E68, #6A1B9A)', minHeight: '100vh', padding: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', marginBottom: 4 }}>
          <Typography variant="h4" sx={{ color: '#fff' }}>
            {searchValue ? `Поиск по запросу: "${searchValue}"` : 'Меню'}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', backgroundColor: 'rgba(255, 255, 255, 0.2)', borderRadius: 2, padding: 1 }}>
            <img src="img/search.svg" alt="Search" style={{ marginRight: 8 }} />
            <TextField
                variant="outlined"
                placeholder="Поиск..."
                value={searchValue}
                onChange={onChangeSearchInput}
                sx={{
                  backgroundColor: 'transparent',
                  '& .MuiOutlinedInput-root': {
                    '& fieldset': {
                      borderColor: 'rgba(255, 255, 255, 0.5)',
                    },
                    '&:hover fieldset': {
                      borderColor: '#fff',
                    },
                    '&.Mui-focused fieldset': {
                      borderColor: '#fff',
                    },
                  },
                  '& .MuiInputBase-input': {
                    color: '#fff',
                    paddingLeft: 0,
                    paddingRight: 0,
                  },
                }}
            />
            {searchValue && (
                <img
                    onClick={() => setSearchValue('')}
                    src="img/btn-remove.svg"
                    alt="Clear"
                    style={{ cursor: 'pointer', marginLeft: 8 }}
                />
            )}
          </Box>
        </Box>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3 }}>
          {renderItems()}
          {isLoading && (
              <Box sx={{ width: '100%', display: 'flex', justifyContent: 'center', marginTop: 4 }}>
                <CircularProgress sx={{ color: '#6A1B9A' }} />
              </Box>
          )}
        </Box>
      </Box>
  );
}

export default Home;
