import React from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import { ThemeProvider, createTheme, CssBaseline, Box } from '@mui/material';
import Header from './components/Header';
import Drawer from './components/Drawer';
import AppContext from './context';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import Orders from './pages/Orders';
import { API_BASE_URL } from "./pages/AuthPage";

// üé® Material 3 –¢–µ–º–∞ —Å —Ñ–∏–æ–ª–µ—Ç–æ–≤–æ–π —Ü–≤–µ—Ç–æ–≤–æ–π —Å—Ö–µ–º–æ–π
const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: { main: '#6A1B9A' },
    secondary: { main: '#9C27B0' },
    background: { default: '#1E1B4B', paper: '#3E1E68' },
    text: { primary: '#ffffff', secondary: '#ccc' },
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          padding: 16,
          backgroundColor: 'rgba(255, 255, 255, 0.1)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          backdropFilter: 'blur(10px)',
        },
      },
    },
  },
});

function App() {
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);
  const [favorites, setFavorites] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');
  const [cartOpened, setCartOpened] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(true);

  React.useEffect(() => {
    async function fetchData() {
      try {
        const [cartResponse, favoritesResponse, itemsResponse] = await Promise.all([
          axios.get(API_BASE_URL + '/foods/cards', {
            headers: { 'Authorization': 'Bearer ' + localStorage.getItem('access_token') }
          }),
          axios.get(API_BASE_URL + '/foods/favorites', {
            headers: { 'Authorization': 'Bearer ' + localStorage.getItem('access_token') }
          }),
          axios.get(API_BASE_URL + '/foods', {
            headers: { 'Authorization': 'Bearer ' + localStorage.getItem('access_token') }
          }),
        ]);

        setIsLoading(false);
        setCartItems(cartResponse.data);
        setFavorites(favoritesResponse.data);
        setItems(itemsResponse.data);
      } catch (error) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–ø—Ä–æ—Å–µ –¥–∞–Ω–Ω—ã—Ö ;(');
        console.error(error);
      }
    }

    fetchData();
  }, []);

  return (
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <AppContext.Provider value={{ items, cartItems, favorites, setCartOpened, setCartItems }}>
          <BrowserRouter>
            <Box sx={{ minHeight: '100vh', backgroundColor: theme.palette.background.default }}>
              <Drawer items={cartItems} onClose={() => setCartOpened(false)} opened={cartOpened} />
              <Header onClickCart={() => setCartOpened(true)} />

              <Routes>
                <Route path="/" element={<Home items={items} cartItems={cartItems} searchValue={searchValue} />} />
                <Route path="/favorites" element={<Favorites />} />
                <Route path="/orders" element={<Orders />} />
              </Routes>
            </Box>
          </BrowserRouter>
        </AppContext.Provider>
      </ThemeProvider>
  );
}

export default App;
