import React from "react";
import { Box, Typography, TextField, InputAdornment, IconButton, Grid, Paper } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import CloseIcon from "@mui/icons-material/Close";
import Card from "../components/Card";

function Home({
                items,
                searchValue,
                setSearchValue,
                onChangeSearchInput,
                onAddToFavorite,
                onAddToCart,
                isLoading,
              }) {
  const renderItems = () => {
    const filteredItems = items.filter((item) =>
        item.title.toLowerCase().includes(searchValue.toLowerCase())
    );

    return (isLoading ? [...Array(8)] : filteredItems).map((item, index) => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
          <Card
              onFavorite={(obj) => onAddToFavorite(obj)}
              onPlus={(obj) => onAddToCart(obj)}
              loading={isLoading}
              {...item}
          />
        </Grid>
    ));
  };

  return (
      <Box
          sx={{
            minHeight: "100vh",
            padding: 4,
            background: "linear-gradient(135deg, #1E1B4B, #3E1E68, #6A1B9A)",
          }}
      >
        <Paper
            elevation={3}
            sx={{
              padding: 3,
              borderRadius: 3,
              backgroundColor: "rgba(255, 255, 255, 0.1)",
              backdropFilter: "blur(10px)",
              border: "1px solid rgba(255, 255, 255, 0.2)",
            }}
        >
          <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 3,
              }}
          >
            <Typography variant="h4" sx={{ color: "#fff", fontWeight: "bold" }}>
              {searchValue ? `Поиск по запросу: "${searchValue}"` : "Меню"}
            </Typography>
            <TextField
                variant="filled"
                placeholder="Поиск..."
                value={searchValue}
                onChange={onChangeSearchInput}
                InputProps={{
                  startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon sx={{ color: "#bbb" }} />
                      </InputAdornment>
                  ),
                  endAdornment: searchValue && (
                      <InputAdornment position="end">
                        <IconButton onClick={() => setSearchValue("")} edge="end">
                          <CloseIcon sx={{ color: "#bbb" }} />
                        </IconButton>
                      </InputAdornment>
                  ),
                  sx: {
                    backgroundColor: "rgba(255, 255, 255, 0.2)",
                    borderRadius: 2,
                    color: "#fff",
                    "& input": { color: "#fff" },
                  },
                }}
                sx={{ width: "300px" }}
            />
          </Box>
          <Grid container spacing={3}>
            {renderItems()}
          </Grid>
        </Paper>
      </Box>
  );
}

export default Home;
