import React, { useState} from 'react';
import axios from 'axios';
import "./AuthPage.css";

export const API_BASE_URL = 'https://spotdiff.ru/fuji-api'

const AuthPage = () => {

    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errorText, setErrorText] = useState("")

    const [isChecked, setIsChecked] = useState(false);

    const handleCheckboxChange = (event) => {
        setIsChecked(event.target.checked);
    };

    async function auth() {
        setErrorText("")

        if(username === "" || password === "") {
            setErrorText("Заполните все поля")
        }else {
            try {
                const response = await axios.post(API_BASE_URL + '/foods/login', {
                    'username': username,
                    'password': password
                })
                localStorage.setItem('access_token', response.data.accessToken)
                window.location.reload()
            }catch (e) {
                console.log(e)
                setErrorText(e)
            }
        }
    }

    async function reg() {
        setErrorText("")

        if(username === "" || password === "") {
            setErrorText("Заполните все поля")
        }else {
            try {
                const response = await axios.post(API_BASE_URL + '/foods/register', {
                    'username': username,
                    'password': password
                })
                localStorage.setItem('access_token', response.data.accessToken)
                window.location.reload()
            }catch (e) {
                console.log(e)
                setErrorText(e)
            }
        }
    }

    return (
        <div className="main">
            <div className="main-block">
                <img src="img/logo.png" style={{width: "90px", height: "90px"}}/>
                <div className="block-info">
                    <div className="button-block">
                        <div className="block-text">
                            <h1>Food App</h1>
                            <p className="p-text-one">Вход в личный кабинет</p>
                            <p className="p-text-error">{errorText}</p>

                            <div className="form-floating">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Имя"
                                    value={username}
                                    onChange={event => setUsername(event.target.value)}
                                />
                                <label htmlFor="firstName">Имя пользователя</label>
                            </div>

                            <div className="form-floating">
                                <input
                                    type="password"
                                    className="form-control"
                                    id="password"
                                    placeholder="Пароль"
                                    value={password}
                                    onChange={event => setPassword(event.target.value)}
                                />
                                <label htmlFor="password">Пароль</label>
                            </div>

                            <div className="form-check">
                                <input
                                    type="checkbox"
                                    className="form-check-input"
                                    id="customCheckbox"
                                    checked={isChecked}
                                    onChange={handleCheckboxChange}
                                />
                                <label className="form-check-label" htmlFor="customCheckbox">
                                    Согласны на рассылку
                                </label>
                            </div>

                            <button id="auth_button" className="a-link" onClick={auth}>Войти</button>
                            <button className="password-reset-button" onClick={reg}>Создать аккаунт</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AuthPage;
