import React from 'react';
import { Card as MuiCard, CardMedia, CardContent, IconButton, Typography, Button, Box } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { useContext } from 'react';
import AppContext from '../../context';

function Card({
                id,
                title,
                image,
                price,
                onFavorite,
                onPlus,
                favorited = false,
                loading = false,
              }) {
  const { isItemAdded } = useContext(AppContext);
  const [isFavorite, setIsFavorite] = React.useState(favorited);
  const obj = { id, parentId: id, title, image, price };

  const onClickPlus = () => {
    onPlus(obj);
  };

  const onClickFavorite = () => {
    onFavorite(obj);
    setIsFavorite(!isFavorite);
  };

  return (
      <MuiCard
          sx={{
            display: 'flex',
            flexDirection: 'column',
            borderRadius: 2,
            boxShadow: 3,
            transition: 'transform 0.3s ease',
            '&:hover': {
              transform: 'scale(1.05)',
              boxShadow: 6,
            },
          }}
      >
        {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', padding: 3 }}>
              <CircularProgress sx={{ color: '#00796b' }} />
            </Box>
        ) : (
            <>
              <Box sx={{ position: 'relative' }}>
                <CardMedia
                    component="img"
                    height="200"
                    image={image}
                    alt={title}
                    sx={{ borderRadius: '10px', objectFit: 'cover' }}
                />
                <IconButton
                    sx={{ position: 'absolute', top: 8, right: 8, backgroundColor: 'rgba(255, 255, 255, 0.7)', borderRadius: '50%' }}
                    onClick={onClickFavorite}
                >
                  {isFavorite ? <FavoriteIcon color="primary" /> : <FavoriteBorderIcon />}
                </IconButton>
              </Box>
              <CardContent sx={{ padding: 2 }}>
                <Typography variant="h6" sx={{ fontWeight: 500, color: '#333' }} gutterBottom>
                  {title}
                </Typography>
                <Typography variant="body2" sx={{ color: '#555' }}>
                  Цена: <strong>{price} руб.</strong>
                </Typography>
                {onPlus && (
                    <Button
                        variant="contained"
                        color={isItemAdded(id) ? 'secondary' : 'primary'}
                        onClick={onClickPlus}
                        sx={{
                          marginTop: 2,
                          borderRadius: 5,
                          fontWeight: 'bold',
                          textTransform: 'none',
                          '&:hover': { backgroundColor: isItemAdded(id) ? '#bdbdbd' : '#00796b' },
                        }}
                    >
                      {isItemAdded(id) ? 'В корзине' : 'Добавить в корзину'}
                    </Button>
                )}
              </CardContent>
            </>
        )}
      </MuiCard>
  );
}

export default Card;
