import React from 'react';
import { Link } from 'react-router-dom';
import { AppBar, Toolbar, Typography, IconButton, Badge, Box } from '@mui/material';
import { useCart } from '../hooks/useCart';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import PersonIcon from '@mui/icons-material/Person';

function Header(props) {
  const { totalPrice } = useCart();

  return (
      <AppBar position="sticky" sx={{ backgroundColor: '#fff', boxShadow: 3 }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Link to="/" style={{ display: 'flex', textDecoration: 'none' }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <img width={40} height={40} src="img/logo.png" alt="Logotype" />
              <Box sx={{ marginLeft: 2 }}>
                <Typography variant="h6" sx={{ color: '#333', fontWeight: 'bold' }}>React Foods</Typography>
                <Typography variant="body2" sx={{ color: '#777' }}>Доставка еды</Typography>
              </Box>
            </Box>
          </Link>

          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <IconButton onClick={props.onClickCart} sx={{ color: '#333' }}>
              <Badge badgeContent={totalPrice ? totalPrice : 0} color="primary">
                <ShoppingCartIcon />
              </Badge>
            </IconButton>

            <Link to="/favorites">
              <IconButton sx={{ color: '#333', marginLeft: 2 }}>
                <FavoriteIcon />
              </IconButton>
            </Link>

            <Link to="/orders">
              <IconButton sx={{ color: '#333', marginLeft: 2 }}>
                <PersonIcon />
              </IconButton>
            </Link>
          </Box>
        </Toolbar>
      </AppBar>
  );
}

export default Header;
